/* Hide Raw Content Container */
#raw-post-payload { display: none !important; }

/* Fix for images inside descriptions getting processed twice */
#ui-app-description img { display: none !important; }

body.script:
//
document.addEventListener('DOMContentLoaded', () => {
    // 1. Inicializar Tema
    const html = document.documentElement;
    const toggle = document.getElementById('theme-toggle');
    const sun = document.getElementById('theme-icon-sun');
    const moon = document.getElementById('theme-icon-moon');

    if (localStorage.getItem('aura-theme') === 'dark' || 
       (!localStorage.getItem('aura-theme') && window.matchMedia('(prefers-color-scheme: dark)').matches)) {
        html.classList.add('dark');
        if(sun) sun.classList.remove('hidden');
        if(moon) moon.classList.add('hidden');
    }

    if(toggle) {
        toggle.addEventListener('click', () => {
            html.classList.toggle('dark');
            const isDark = html.classList.contains('dark');
            localStorage.setItem('aura-theme', isDark ? 'dark' : 'light');
            if(isDark) {
                sun.classList.remove('hidden');
                moon.classList.add('hidden');
            } else {
                sun.classList.add('hidden');
                moon.classList.remove('hidden');
            }
        });
    }

    // 2. Función Búsqueda
    window.toggleSearch = () => {
        const el = document.getElementById('search-overlay');
        if(el) {
            el.classList.toggle('hidden');
            if(!el.classList.contains('hidden')) {
                const inp = el.querySelector('input');
                if(inp) setTimeout(() => inp.focus(), 100);
            }
        }
    };

    // 3. LÓGICA PRINCIPAL: GRID vs DETALLE
    const detailContext = document.querySelector('.app-detail-context');

    if (detailContext) {
        // --- MODO DETALLE (POST) ---
        const rawContainer = document.getElementById('raw-post-payload');
        if (!rawContainer) return;

        // A. Procesar Imágenes (Icono y Carrusel)
        const imgs = rawContainer.querySelectorAll('img');
        if (imgs.length > 0) {
            // Icono = Primera imagen
            const iconEl = document.getElementById('ui-app-icon');
            if (iconEl) iconEl.src = imgs[0].src;

            // Carrusel = Todas las imágenes
            const carousel = document.getElementById('ui-screenshots-container');
            const previewSec = document.getElementById('ui-preview-section');

            imgs.forEach(img => {
                const slide = document.createElement('img');
                slide.src = img.src;
                slide.className = "h-[300px] md:h-[450px] rounded-[1.75rem] shadow-soft snap-center border border-gray-100 dark:border-white/10 object-cover shrink-0 select-none bg-gray-100 dark:bg-white/5";
                if(carousel) carousel.appendChild(slide);
            });

            if(previewSec) previewSec.classList.remove('hidden');
        }

        // B. Procesar Descripción (Buscar clase específica o todo el texto)
        const uiDesc = document.getElementById('ui-app-description');
        const sourceDesc = rawContainer.querySelector('.tm-single-desc__wrap');

        if (uiDesc) {
            if (sourceDesc) {
                uiDesc.innerHTML = sourceDesc.innerHTML;
            } else {
                // Fallback: Si no encuentra la clase, coge todo el HTML del post (menos lo que ya procesamos)
                uiDesc.innerHTML = rawContainer.innerHTML; 
            }

            // LIMPIEZA DE ESTILOS INLINE (Crucial para Dark Mode)
            uiDesc.querySelectorAll('*').forEach(el => {
                el.removeAttribute('style'); // Quita background: white, color: black, etc.
                el.classList.add('mb-4', 'leading-relaxed'); 
            });

            // Ocultar elementos que no queremos en la descripción (como el bloque de info duplicado)
            const infoInDesc = uiDesc.querySelector('.info-section');
            if(infoInDesc) infoInDesc.style.display = 'none';
            const dlBtnInDesc = uiDesc.querySelector('.item-download');
            if(dlBtnInDesc) dlBtnInDesc.style.display = 'none';
        }

        // C. Procesar Info (Developer, Size, etc)
        const sourceInfo = rawContainer.querySelector('.info-section');
        if (sourceInfo) {
            const listItems = sourceInfo.querySelectorAll('li');
            const devEl = document.getElementById('ui-info-dev');
            const sizeEl = document.getElementById('ui-info-size');
            const compatEl = document.getElementById('ui-info-compat');

            listItems.forEach(li => {
                const txt = li.textContent;
                const val = txt.split(':')[1]?.trim();
                if(!val) return;

                if (txt.toLowerCase().includes('developer') && devEl) devEl.textContent = val;
                if (txt.toLowerCase().includes('size') && sizeEl) sizeEl.textContent = val;
                if (txt.toLowerCase().includes('compatibility') && compatEl) compatEl.textContent = val;
            });
        }

        // D. Procesar Botón de Descarga y Precio
        const dlBtn = document.getElementById('ui-main-download-btn');
        const sourceLink = rawContainer.querySelector('a.item-download');

        // Leer etiquetas para el precio
        const metaDiv = document.querySelector('.hidden-meta');
        let priceLabel = 'GET';
        let categoryLabel = 'App';

        if(metaDiv) {
            const labels = metaDiv.querySelectorAll('.meta-label');
            labels.forEach(l => {
                const txt = l.textContent.trim().toLowerCase();
                if(txt.startsWith('price:')) {
                    const p = txt.split(':')[1];
                    priceLabel = (p === '0' || p === 'free') ? 'GET' : '$' + p;
                } else if(!txt.includes(':') && txt !== 'notice') {
                    categoryLabel = l.textContent;
                }
            });
        }

        // Actualizar UI Botón y Categoría
        if(dlBtn) {
            const span = dlBtn.querySelector('span');
            if(span) span.textContent = priceLabel;

            if(sourceLink) {
                dlBtn.onclick = (e) => {
                    e.preventDefault();
                    window.open(sourceLink.href, '_blank');
                };
            } else {
                 // Si no hay link, deshabilitar visualmente
                 dlBtn.classList.add('opacity-50', 'cursor-not-allowed');
            }
        }

        const catDetail = document.querySelector('.ui-app-category-detail');
        if(catDetail) catDetail.textContent = categoryLabel;

    } else {
        // --- MODO GRID (HOME) ---
        // Hidratar tarjetas con datos de etiquetas
        document.querySelectorAll('.app-card').forEach(card => {
            const meta = card.querySelector('.hidden-meta');
            if(!meta) return;

            let price = 'GET';
            let rating = '4.5';
            let cat = 'App';

            meta.querySelectorAll('.meta-label').forEach(l => {
                const t = l.textContent.trim().toLowerCase();
                if(t.startsWith('price:')) {
                     const p = t.split(':')[1];
                     price = (p === '0' || p === 'free') ? 'GET' : '$' + p;
                } else if(t.startsWith('rating:')) rating = t.split(':')[1];
                else if(!t.includes(':') && t !== 'notice') cat = l.textContent;
            });

            const pBtn = card.querySelector('.app-price-btn');
            if(pBtn) pBtn.textContent = price;

            const rEl = card.querySelector('.app-rating');
            if(rEl) rEl.textContent = rating;

            const cEl = card.querySelector('.app-category');
            if(cEl) cEl.textContent = cat;
        });
    }
});
//
